#!/usr/bin/env node
/**
 * Bundle the audio-inline-entry.ts into a self-contained JavaScript string.
 *
 * This script uses esbuild to:
 * 1. Bundle audio-inline-entry.ts with all dependencies (WebAudioEngine)
 * 2. Output an IIFE that exposes setupAudioBridge globally
 * 3. Convert the bundled JS to a TypeScript string export
 */

import { build } from 'esbuild';
import { writeFileSync, mkdirSync, existsSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const ENTRY_FILE = join(__dirname, '..', 'src', 'audio', 'audio-inline-entry.ts');
const OUTPUT_DIR = join(__dirname, '..', 'src', 'audio');
const OUTPUT_TS = join(OUTPUT_DIR, 'audio-inline.generated.ts');

async function bundleAudioInline() {
  console.log('Bundling audio-inline-entry.ts...');

  // Bundle with esbuild
  const result = await build({
    entryPoints: [ENTRY_FILE],
    bundle: true,
    format: 'iife',
    globalName: 'AudioInline',
    minify: false, // Keep readable for debugging
    sourcemap: false,
    write: false, // Don't write to disk, capture output
    target: 'es2020',
    platform: 'browser',
    // Don't externalize anything - bundle everything into one file
    external: [],
  });

  if (result.outputFiles.length === 0) {
    throw new Error('esbuild produced no output');
  }

  const bundledCode = result.outputFiles[0].text;

  // Escape backticks and ${} for template literal
  const escapedCode = bundledCode
    .replace(/\\/g, '\\\\')
    .replace(/`/g, '\\`')
    .replace(/\$\{/g, '\\${');

  // Generate TypeScript export
  const tsContent = `/* eslint-disable max-lines */
/**
 * Auto-generated from audio-inline-entry.ts
 * DO NOT EDIT THIS FILE DIRECTLY - edit the .ts source instead.
 *
 * Bundled audio bridge for standalone HTML exports.
 * Run "npm run build:audio" to regenerate this file.
 */
export const AUDIO_INLINE_JS = \`${escapedCode}\`
`;

  // Ensure output directory exists
  if (!existsSync(OUTPUT_DIR)) {
    mkdirSync(OUTPUT_DIR, { recursive: true });
  }

  writeFileSync(OUTPUT_TS, tsContent);
  console.log(`Generated: audio-inline.generated.ts (${bundledCode.length} bytes)`);
}

bundleAudioInline().catch((err) => {
  console.error('Failed to bundle audio inline:', err);
  process.exit(1);
});
