import { describe, it, expect } from 'vitest'
import { parseCommand } from '../src/parseCommand'

describe('parseCommand', () => {
  describe('basic parsing', () => {
    it('should parse a simple command with no arguments', () => {
      const result = parseCommand('ls')
      expect(result.command).toBe('ls')
      expect(result.args).toEqual([])
      expect(result.raw).toBe('ls')
    })

    it('should parse a command with one argument', () => {
      const result = parseCommand('cd /home')
      expect(result.command).toBe('cd')
      expect(result.args).toEqual(['/home'])
    })

    it('should parse a command with multiple arguments', () => {
      const result = parseCommand('cp file1.txt file2.txt /backup')
      expect(result.command).toBe('cp')
      expect(result.args).toEqual(['file1.txt', 'file2.txt', '/backup'])
    })

    it('should handle extra whitespace between arguments', () => {
      const result = parseCommand('ls   -la    /home')
      expect(result.command).toBe('ls')
      expect(result.args).toEqual(['-la', '/home'])
    })

    it('should trim leading and trailing whitespace', () => {
      const result = parseCommand('  ls -la  ')
      expect(result.command).toBe('ls')
      expect(result.args).toEqual(['-la'])
    })
  })

  describe('empty and whitespace input', () => {
    it('should return empty command for empty string', () => {
      const result = parseCommand('')
      expect(result.command).toBe('')
      expect(result.args).toEqual([])
    })

    it('should return empty command for whitespace only', () => {
      const result = parseCommand('   ')
      expect(result.command).toBe('')
      expect(result.args).toEqual([])
    })
  })

  describe('quoted arguments', () => {
    it('should handle double-quoted argument with spaces', () => {
      const result = parseCommand('echo "hello world"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello world'])
    })

    it('should handle single-quoted argument with spaces', () => {
      const result = parseCommand("echo 'hello world'")
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello world'])
    })

    it('should handle multiple quoted arguments', () => {
      const result = parseCommand('echo "hello" "world"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello', 'world'])
    })

    it('should handle mixed quoted and unquoted arguments', () => {
      const result = parseCommand('cp "my file.txt" /backup')
      expect(result.command).toBe('cp')
      expect(result.args).toEqual(['my file.txt', '/backup'])
    })

    it('should handle empty quoted strings', () => {
      const result = parseCommand('echo "" ""')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['', ''])
    })

    it('should preserve quotes inside other quotes', () => {
      const result = parseCommand('echo "he said \'hello\'"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(["he said 'hello'"])
    })

    it('should preserve double quotes inside single quotes', () => {
      const result = parseCommand("echo 'he said \"hello\"'")
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['he said "hello"'])
    })

    it('should handle unclosed double quotes', () => {
      const result = parseCommand('echo "hello')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello'])
    })

    it('should handle unclosed single quotes', () => {
      const result = parseCommand("echo 'hello")
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello'])
    })

    it('should concatenate adjacent quoted strings', () => {
      const result = parseCommand('echo "hello""world"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['helloworld'])
    })

    it('should concatenate mixed adjacent quotes', () => {
      const result = parseCommand("echo \"hello\"'world'")
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['helloworld'])
    })
  })

  describe('escape sequences', () => {
    it('should handle escaped spaces', () => {
      const result = parseCommand('cd my\\ folder')
      expect(result.command).toBe('cd')
      expect(result.args).toEqual(['my folder'])
    })

    it('should handle escaped quotes', () => {
      const result = parseCommand('echo \\"hello\\"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['"hello"'])
    })

    it('should handle escaped backslash', () => {
      const result = parseCommand('echo \\\\path')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['\\path'])
    })

    it('should treat backslash literally inside single quotes (bash-compatible)', () => {
      const result = parseCommand("echo 'path\\to\\file'")
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['path\\to\\file'])
    })

    it('should preserve trailing backslash', () => {
      const result = parseCommand('echo hello\\')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello\\'])
    })
  })

  describe('special characters', () => {
    it('should handle arguments with dashes', () => {
      const result = parseCommand('ls -la --all')
      expect(result.command).toBe('ls')
      expect(result.args).toEqual(['-la', '--all'])
    })

    it('should handle arguments with equals signs', () => {
      const result = parseCommand('export VAR=value')
      expect(result.command).toBe('export')
      expect(result.args).toEqual(['VAR=value'])
    })

    it('should handle paths with dots', () => {
      const result = parseCommand('cd ../folder')
      expect(result.command).toBe('cd')
      expect(result.args).toEqual(['../folder'])
    })

    it('should treat tabs as separators', () => {
      const result = parseCommand('echo\thello\tworld')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello', 'world'])
    })

    it('should handle mixed spaces and tabs', () => {
      const result = parseCommand('echo \t hello \t world')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello', 'world'])
    })

    it('should preserve tabs inside quotes', () => {
      const result = parseCommand('echo "hello\tworld"')
      expect(result.command).toBe('echo')
      expect(result.args).toEqual(['hello\tworld'])
    })
  })

  describe('raw preservation', () => {
    it('should preserve the original raw command', () => {
      const input = '  echo   "hello world"  '
      const result = parseCommand(input)
      expect(result.raw).toBe(input)
    })
  })
})

tests/pathUtils.test.ts.source:
import { describe, it, expect } from 'vitest'
import {
  normalizePath,
  joinPath,
  resolvePath,
  isAbsolutePath,
  getParentPath,
  getBasename,
} from '../src/pathUtils'

describe('pathUtils', () => {
  describe('normalizePath', () => {
    it('should return "/" for empty string', () => {
      expect(normalizePath('')).toBe('/')
    })

    it('should return "/" for root path', () => {
      expect(normalizePath('/')).toBe('/')
    })

    it('should normalize backslashes to forward slashes', () => {
      expect(normalizePath('foo\\bar\\baz')).toBe('/foo/bar/baz')
    })

    it('should remove trailing slashes', () => {
      expect(normalizePath('/foo/bar/')).toBe('/foo/bar')
    })

    it('should handle multiple consecutive slashes', () => {
      expect(normalizePath('/foo//bar///baz')).toBe('/foo/bar/baz')
    })

    it('should resolve single dot segments', () => {
      expect(normalizePath('/foo/./bar')).toBe('/foo/bar')
    })

    it('should resolve double dot segments', () => {
      expect(normalizePath('/foo/bar/../baz')).toBe('/foo/baz')
    })

    it('should not go above root with double dots', () => {
      expect(normalizePath('/foo/../../bar')).toBe('/bar')
      expect(normalizePath('/../..')).toBe('/')
    })

    it('should handle complex paths', () => {
      expect(normalizePath('/foo/./bar/../baz/./qux/..')).toBe('/foo/baz')
    })

    it('should add leading slash for relative paths', () => {
      expect(normalizePath('foo/bar')).toBe('/foo/bar')
    })
  })

  describe('joinPath', () => {
    it('should join two path segments', () => {
      expect(joinPath('/foo', 'bar')).toBe('/foo/bar')
    })

    it('should handle absolute second path', () => {
      expect(joinPath('/foo', '/bar')).toBe('/bar')
    })

    it('should handle empty base', () => {
      expect(joinPath('', 'foo')).toBe('/foo')
    })

    it('should handle empty path', () => {
      expect(joinPath('/foo', '')).toBe('/foo')
    })

    it('should join multiple segments', () => {
      expect(joinPath('/foo', 'bar', 'baz')).toBe('/foo/bar/baz')
    })

    it('should normalize the result', () => {
      expect(joinPath('/foo/', '/bar/../baz')).toBe('/baz')
    })

    it('should return root for no arguments', () => {
      expect(joinPath()).toBe('/')
    })

    it('should return root for all empty arguments', () => {
      expect(joinPath('', '', '')).toBe('/')
    })
  })

  describe('resolvePath', () => {
    it('should resolve absolute path as-is', () => {
      expect(resolvePath('/base', '/absolute')).toBe('/absolute')
    })

    it('should resolve relative path against base', () => {
      expect(resolvePath('/base/dir', 'file.txt')).toBe('/base/dir/file.txt')
    })

    it('should resolve parent references', () => {
      expect(resolvePath('/base/dir', '../other')).toBe('/base/other')
    })

    it('should handle current directory reference', () => {
      expect(resolvePath('/base/dir', './file')).toBe('/base/dir/file')
    })

    it('should normalize the result', () => {
      expect(resolvePath('/base//dir/', './foo/../bar')).toBe('/base/dir/bar')
    })
  })

  describe('isAbsolutePath', () => {
    it('should return true for paths starting with /', () => {
      expect(isAbsolutePath('/foo')).toBe(true)
      expect(isAbsolutePath('/')).toBe(true)
    })

    it('should return false for relative paths', () => {
      expect(isAbsolutePath('foo')).toBe(false)
      expect(isAbsolutePath('foo/bar')).toBe(false)
      expect(isAbsolutePath('./foo')).toBe(false)
      expect(isAbsolutePath('../foo')).toBe(false)
    })

    it('should return false for empty string', () => {
      expect(isAbsolutePath('')).toBe(false)
    })
  })

  describe('getParentPath', () => {
    it('should return parent directory', () => {
      expect(getParentPath('/foo/bar')).toBe('/foo')
    })

    it('should return root for root children', () => {
      expect(getParentPath('/foo')).toBe('/')
    })

    it('should return root for root', () => {
      expect(getParentPath('/')).toBe('/')
    })

    it('should handle trailing slashes', () => {
      expect(getParentPath('/foo/bar/')).toBe('/foo')
    })

    it('should handle deep paths', () => {
      expect(getParentPath('/a/b/c/d')).toBe('/a/b/c')
    })
  })

  describe('getBasename', () => {
    it('should return the last segment', () => {
      expect(getBasename('/foo/bar')).toBe('bar')
    })

    it('should handle root path', () => {
      expect(getBasename('/')).toBe('')
    })

    it('should handle single segment', () => {
      expect(getBasename('/foo')).toBe('foo')
    })

    it('should handle trailing slashes', () => {
      expect(getBasename('/foo/bar/')).toBe('bar')
    })

    it('should handle file names with extensions', () => {
      expect(getBasename('/path/to/file.txt')).toBe('file.txt')
    })
  })
})

